use labsqldb;

drop table if exists [SalesLT].[ProductAnalytics];

create table [SalesLT].[ProductAnalytics] (
  ProductID int not null,
  Name nvarchar(50) not null,
  total int not null
);

insert into [SalesLT].[ProductAnalytics]
select ord.ProductID, prod.Name, sum(ord.OrderQty) total
from SalesLT.SalesOrderDetail ord join SalesLT.Product prod on ord.ProductID = prod.ProductID
group by ord.ProductID, prod.Name
order by total desc;
